function(argMap, hint, formatter) {
    var timeString = formatter.dateAndTimeToString(argMap.start_time);

    var title;
    if (argMap.has_person)
        title = `Person detected on your camera at ${timeString}`;
    else if (argMap.has_sound && argMap.has_motion)
        title = `Sound and motion detected on your camera at ${timeString}`;
    else if (argMap.has_sound)
        title = `Sound detected on your camera at ${timeString}`;
    else if (argMap.has_motion)
        title = `Motion detected on your camera at ${timeString}`;
    else
        title = `Something detected on your camera at ${timeString}`;
    return title;
}
